targets.expr:
100 *
sum by (name) (
  rate(
    container_cpu_usage_seconds_total{
      job="cadvisor",
      image!="",
      container_label_com_docker_compose_project="$stack",
      container_label_com_docker_compose_service="$service",
      name="$container"
    }[5m]
  )
)
/ scalar(
  count(node_cpu_seconds_total{job="node", mode="idle"})
)

sum by (name) (
  rate(
    container_fs_reads_bytes_total{
      job="cadvisor",
      image!="",
      container_label_com_docker_compose_project="$stack",
      container_label_com_docker_compose_service="$service",
      name="$container"
    }[5m]
  )
+ rate(
    container_fs_writes_bytes_total{
      job="cadvisor",
      image!="",
      container_label_com_docker_compose_project="$stack",
      container_label_com_docker_compose_service="$service",
      name="$container"
    }[5m]
  )
)

sum by (name) (
  rate(
    container_network_receive_bytes_total{
      job="cadvisor",
      image!="",
      container_label_com_docker_compose_project="$stack",
      container_label_com_docker_compose_service="$service",
      name="$container"
    }[5m]
  )
+ rate(
    container_network_transmit_bytes_total{
      job="cadvisor",
      image!="",
      container_label_com_docker_compose_project="$stack",
      container_label_com_docker_compose_service="$service",
      name="$container"
    }[5m]
  )
)
